/// Look up the VexFlow glyph outline for a time signature digit (0-9).
pub(super) fn timesig_digit_glyph(d: u32) -> &'static str {
    match d {
        0 => TIMESIG_DIGIT_0,
        1 => TIMESIG_DIGIT_1,
        2 => TIMESIG_DIGIT_2,
        3 => TIMESIG_DIGIT_3,
        4 => TIMESIG_DIGIT_4,
        5 => TIMESIG_DIGIT_5,
        6 => TIMESIG_DIGIT_6,
        7 => TIMESIG_DIGIT_7,
        8 => TIMESIG_DIGIT_8,
        9 => TIMESIG_DIGIT_9,
        _ => TIMESIG_DIGIT_0,
    }
}

/// Compute the width (in pixels) of a multi-digit number at TIMESIG_GLYPH_SCALE.
pub(super) fn timesig_number_width(n: i32) -> f64 {
    let s = TIMESIG_GLYPH_SCALE;
    let digits = if n == 0 { vec![0u32] } else {
        let mut d = Vec::new();
        let mut v = n.unsigned_abs();
        while v > 0 {
            d.push(v % 10);
            v /= 10;
        }
        d.reverse();
        d
    };
    digits.iter().map(|&d| TIMESIG_DIGIT_HA[d as usize] * s).sum()
}

